mapper.select:
select distinct dongName, dongCode
from dongcode
where left(dongCode, 5) = #{gugun}
order by dongName;

with tee as (select house_deal.addressId, address.apartName, address.dongCode, address.buildYear, house_deal.dealAmount, house_deal.dealYear, house_deal.dealMonth, house_deal.dealDay, house_deal.area, house_deal.floor, address.roadName, address.roadNameBonbun, address.roadNameBubun from address left join house_deal on house_deal.addressId = address.addressId)
select * from tee where addressId in (select addressId from address where dongCode = #{dongCode})


			and convert(tee.dealAmount, signed) <= #{word}


			and convert(tee.area, signed) >= #{word}


			and convert(tee.floor, signed) >= #{word}


			and tee.buildYear >= #{word}


			and tee.apartName like concat('%', #{word}, '%')


			and tee.dealYear = #{dealYear} and tee.dealMonth = #{dealMonth}

mapper.insert:
insert into house_product (userId, addressId, floor, buildYear, dealAmount, area, dealType, stateFlag, content)
values (#{userId}, #{addressId}, #{floor}, #{buildYear}, #{dealAmount}, #{area}, #{dealType}, 0, #{content}); 

	select last_insert_id()

mapper.select:
with temp as (select house_product.houseProductId, house_product.userId, house_product.addressId, house_product.floor, house_product.buildYear, house_product.dealAmount, house_product.area, house_product.dealType, house_product.stateFlag, house_product.content, address.roadName, address.roadNameBonbun, address.roadNameBubun, address.apartName, address.dongCode from address left join house_product on house_product.addressId = address.addressId)
select * from temp where addressId in (select addressId from address where dongCode = #{dongCode})


		and convert(dealAmount, signed) <= #{word}


		and convert(area, signed) >= #{word}


		and convert(floor, signed) >= #{word}


		and buildYear >= #{word}


		and userId = #{word}


		and dealType = #{word}


order by houseProductId desc

select houseImageId, houseProductId, saveFolder, originalFileName, saveFileName
from house_image
where houseProductId = #{houseProductId};

mapper.update:
update house_product
set addressId = #{addressId}, floor = #{floor}, buildYear = #{buildYear}, dealAmount = #{dealAmount}, area = #{area}, dealType = #{dealType}, content = #{content}
where houseProductId = #{houseProductId}

update house_product
set stateFlag = 1
where houseProductId = #{houseProductid}

update user
set reportCount = reportCount + 1
where userId = #{userid}

mapper.select:
select houseProductReviewId, houseProductId, userId, writerUserId, rating, content, image, regtime
from house_product_review
where houseProductId = #{houseProductid};

mapper.update:
update house_product_review
set rating = #{rating}, content = #{content}, image = #{image}
where houseProductReviewId = #{houseProductReviewId}
