mapper.select:
select count(articleno) from board_notice
where subject like concat('%', #{word}, '%')
where ${key} = #{word}

select articleno, userid, subject, content, hit, regitime
from board_notice
where articleno = #{articleNo}

mapper.update:
update board_notice
set hit = hit + 1
where articleno = #{articleNo}

update board_notice
set subject = #{subject}, content = #{content}
where articleno = #{articleNo}

mapper.select:
select count(articleno) from board_report
where subject like concat('%', #{word}, '%')
where ${key} = #{word}

select articleno, userid, subject, content, hit, regitime
from board_report
where articleno = #{articleno}

select boardReportImageId, articleNo, saveFolder, originalFileName, saveFileName
from board_report_image
where articleNo = #{articleno}

mapper.update:
update board_report
set hit = hit + 1
where articleno = #{articleno}

update board_report
set subject = #{subject}, content = #{content}
where articleno = #{articleNo}
