mapper.select:
select count(articleno) from board_notice
where subject like concat('%', #{word}, '%')
where ${key} = #{word}

select articleno, userid, subject, content, hit, regitime
from board_notice
where articleno = #{articleNo}

mapper.update:
update board_notice
set hit = hit + 1
where articleno = #{articleNo}

update board_notice
set subject = #{subject}, content = #{content}
where articleno = #{articleNo}
