mapper.select:
select count(userid)
from user
where userid = #{userid};

select userid, username, email, phonenumber
from user
where userid = #{userId} and userpwd = #{userPwd}

select userid, userName, email, phonenumber, profileimage
from user
where userid = #{userid}

select token
from user
where userid = #{userid}

mapper.update:
update user
set token = #{token}
where userid = #{userId}

update user
set token = #{token, jdbcType=VARCHAR}
where userid = #{userId}
